  /**
   * ãƒ­ãƒ¼ãƒ€ãƒ¼
   * @param {string} target - ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’æŒ¿å…¥ã™ã‚‹ã‚¿ãƒ¼ã‚²ãƒƒãƒˆï¼ˆæŒ‡å®šãŒãªã‘ã‚Œã°bodyï¼‰
   * @param {string} msg - ãƒ­ãƒ¼ãƒ€ãƒ¼ã®ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ï¼ˆã‚­ãƒ£ãƒ—ã‚·ãƒ§ãƒ³ï¼‰ default: 'Loading...'
   * @param {string} size - ãƒ­ãƒ¼ãƒ€ãƒ¼ã®ã‚µã‚¤ã‚ºï¼ˆã‚¹ã‚¿ã‚¤ãƒ«ã‚·ãƒ¼ãƒˆã‚’å‚ç…§ï¼‰ default: 'md'
   * @param {string} style - ãƒ­ãƒ¼ãƒ€ãƒ¼ã®ã‚¹ã‚¿ã‚¤ãƒ«ï¼ˆã‚¹ã‚¿ã‚¤ãƒ«ã‚·ãƒ¼ãƒˆã‚’å‚ç…§ï¼‰ default: '--bg-black'
   */
  class RfLoader {
    #settings = {
      target: document.body,
      msg: 'Loading...',
      size: 'md',
      style: '--bg-black',
      loaderClass: 'c-loader',
      loader: null,
    }
    constructor(target, msg, size, style) {
      if (target) this.#settings.target = target;
      if (msg) this.#settings.msg = msg;
      if (size) this.#settings.size = size;
      if (style) this.#settings.style = style;
      console.log(this.#settings);
      this.#init();
    }

    // ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’æŒ¿å…¥ï¼ˆã‚³ãƒ³ã‚¹ãƒˆãƒ©ã‚¯ã‚¿ã‹ã‚‰å‘¼ã³å‡ºã—ï¼‰
    #insertLoader(loader) {
      console.log('ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’æŒ¿å…¥');

      if(typeof loader === 'string') {
        this.#settings.target.insertAdjacentHTML('beforeend', loader);
      } else if(typeof loader === 'object') {
        // 2å›ç›®ä»¥é™ã¯ã“ã£ã¡
        this.#settings.target.insertAdjacentElement('beforeend', loader);
      }
      if(!this.#settings.loader) {
        this.#settings.loader = this.#settings.target.querySelector('.' + this.#settings.loaderClass); //ãƒ­ãƒ¼ãƒ€ãƒ¼è¦ç´ ã‚’ä¿ç®¡
      }
    }

    // ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’å‰Šé™¤ï¼ˆpublicï¼‰
    removeLoader() {
      console.log('ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’å‰Šé™¤');
      this.#settings.loader.remove();
    }

    // ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’å†æŒ¿å…¥ï¼ˆpublicï¼‰
    reinsertLoader() {
      console.log('ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’å†æŒ¿å…¥');
      this.#insertLoader(this.#settings.loader);
    }

    // ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’ä½œæˆ
    #makeLoader() {
      console.log('ãƒ­ãƒ¼ãƒ€ãƒ¼ã‚’ä½œæˆ');
      let size = '';
      if (this.#settings.size !== 'md') size = `--${this.#settings.size}`;
      const loader = `<div class="${this.#settings.loaderClass} ${this.#settings.style} ${size}"></div>`;
      this.#insertLoader(loader);
    }

    #init() {
      this.#makeLoader();
    }
  }

  /**
   * Checkbox Status - ãƒã‚§ãƒƒã‚¯ãƒœãƒƒã‚¯ã‚¹ã®ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã‚’ç¢ºèªã™ã‚‹
   * constructor
   * @param {HTMLFormElement} form
   * @param {HTMLInputElement[]} checkboxes
   */
  class RfCheckboxStatus {
    #form;
    #checkboxes;
    #validator = {
      get(target, key) {
        console.log("target&key: ", target, key);
        const status = Array.prototype.slice
          .call(target["elements"])
          .some((x) => x.checked);
        console.log(target["elements"]);
        console.log(status);
        target[key] = status;
        return target[key];
      }
    };
    #checkBoxObj = {
      status: null,
      elements: null
    };
    #store;

    constructor(form, checkboxes) {
      if (!form || !checkboxes) throw new Error("No Form selected!");
      console.log("yes checkboxes!");
      this.#form = form;
      this.#checkboxes = checkboxes;
      this.#checkBoxObj["elements"] = this.#checkboxes;
      this.#store = new Proxy(this.#checkBoxObj, this.#validator);
      // this.#cb = cb
    }


    getStatus() {
      return this.#store.status;
    }
  }

  class RfModals {
    #modalOpenHtmlClass = '--is-modal-open'
    #modalOpenClass = '--is-open'
    #html = document.documentElement
    #modals
    #modalOpenBtns
    #modalAjaxOpenBtns
    #modalTargetName = 'jsModalTarget'
    #openedModals = []

    /**
     * Initializes a new instance of the RfModals class.
     *
     * @param {Object|null} modals - Optional array of modal elements to be added dynamically.
     * @example {modal: data-js-modal-target(ãƒ¢ãƒ¼ãƒ€ãƒ«æœ¬ä½“), modalOpenBtn: data-js-modal(ãƒ¢ãƒ¼ãƒ€ãƒ«é–‹ããƒœã‚¿ãƒ³)}
     * @return {void}
     */
    constructor(modals = null) {
      // ã“ã®ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆãŒåˆæœŸåŒ–ã•ã‚Œã¦ã„ãªã„å ´åˆã«ã€ãƒ€ã‚¤ãƒŠãƒŸãƒƒã‚¯ã«è¿½åŠ ã•ã‚Œã‚‹ãƒ¢ãƒ¼ãƒ€ãƒ«è¦ç´ ã‚’è¿½åŠ ã™ã‚‹ã®ã§ã‚ã‚Œã°è¿½åŠ ã€‚
      if(modals) {
        this.modal(modals);
        return
      }

      this.#modals = Array.from(document.querySelectorAll('[data-js-modal-target]'));
      this.#modalOpenBtns = Array.from(document.querySelectorAll('[data-js-modal]'));
      this.#modalAjaxOpenBtns = Array.from(document.querySelectorAll('[data-js-ajax-modal]'));

      this.#setModalOpenBtns();
      this.#setModals();
    }

    // ===

    // ãƒ¢ãƒ¼ãƒ€ãƒ«ã‚’é–‹ã
    openModal(modalId) {
      const targetModal = document.querySelector(modalId);
      console.dir(targetModal);
      targetModal.classList.add(this.#modalOpenClass); //ãƒ¢ãƒ¼ãƒ€ãƒ«æœ¬ä½“ã«ã‚¯ãƒ©ã‚¹ã‚’è¿½åŠ 
      this.#html.classList.add(this.#modalOpenHtmlClass); //htmlã‚¿ã‚°ã«ã‚¯ãƒ©ã‚¹ã‚’è¿½åŠ 
      this.#openedModals.push(modalId); //ãƒ¢ãƒ¼ãƒ€ãƒ«é–‹ããƒªã‚¹ãƒˆã«è¿½åŠ 
      console.log('opened modals', this.#openedModals);
    }

    // é–‹ããƒœã‚¿ãƒ³ã«ã‚¯ãƒªãƒƒã‚¯ã‚¤ãƒ™ãƒ³ãƒˆã‚’è¨­å®š
    #setModalOpenBtnEvent(btn) {
      const modalId = btn.dataset.jsModal;
      btn.addEventListener('click', (e) => {
        if (btn.tagName === 'A' || btn.tagName === 'BUTTON') {
          e.preventDefault();
        }
        
        this.openModal(modalId);
      });
    }

    // é–‹ããƒœã‚¿ãƒ³ã®è¨­å®š
    #setModalOpenBtns() {
      this.#modalOpenBtns.forEach(btn => {
        this.#setModalOpenBtnEvent(btn);
      });
    }

    // ===

    // ãƒ¢ãƒ¼ãƒ€ãƒ«ã‚’é–‰ã˜ã‚‹
    #closeModal(modal) {
      // console.log('ãƒ¢ãƒ¼ãƒ€ãƒ«é–‰ã˜ã‚‹, openedModalsã¯?', openedModals)
      // console.log(modal.id)
      // console.log(modal instanceof Element)
      const index = this.#openedModals.indexOf(modal);
      this.#openedModals.splice(index, 1);
      // console.log('ãƒ¢ãƒ¼ãƒ€ãƒ«é–‰ã˜ãŸã‚ã¨, openedModalsã¯?', openedModals)
      modal.classList.remove(this.#modalOpenClass);

      // ãƒ¢ãƒ¼ãƒ€ãƒ«ãŒå¤šé‡ã«é–‹ã„ã¦ã„ã‚‹å ´åˆã€ã™ã¹ã¦ã®ãƒ¢ãƒ¼ãƒ€ãƒ«ãŒé–‰ã˜ã¦ã„ã‚‹ã“ã¨ã‚’ãƒã‚§ãƒƒã‚¯ã—ã¦HTMLã‹ã‚‰ã‚‚ã‚¯ãƒ©ã‚¹ã‚’å‰Šé™¤ã™ã‚‹
      if (this.#openedModals.length === 0) {
        this.#html.classList.remove(this.#modalOpenHtmlClass);
      }

      console.log('closing...opened modals', this.#openedModals);
    }

    // ===

    // ãƒ¢ãƒ¼ãƒ€ãƒ«ã‚’é–‰ã˜ã‚‹ã‚¤ãƒ™ãƒ³ãƒˆè¨­å®š
    #setModalCloseEvent(modal) {
      // ãƒ¢ãƒ¼ãƒ€ãƒ«æœ¬ä½“é–‰ã˜ã‚‹(ãƒ¢ãƒ¼ãƒ€ãƒ«ã®ãªã«ã‚‚ãªã„ã¨ã“ã‚ã‚’ã‚¯ãƒªãƒƒã‚¯ã—ãŸã¨ãé–‰ã˜ã‚‹)
      modal.addEventListener('click', e => {
        if (this.#modalTargetName in e.target.dataset) {
          console.log('ãƒ¢ãƒ¼ãƒ€ãƒ«ã‚¿ãƒ¼ã‚²ãƒƒãƒˆ');
          this.#closeModal(modal);
        }
      });

      // ãƒ¢ãƒ¼ãƒ€ãƒ«é–‰ã˜ã‚‹ãƒœã‚¿ãƒ³ã®è¨­å®š(ãƒ¢ãƒ¼ãƒ€ãƒ«æœ¬ä½“ã‹ã‚‰ãƒ¢ãƒ¼ãƒ€ãƒ«é–‰ã˜ã‚‹ãƒœã‚¿ãƒ³ã‚’æ¢ã—ã¦è¨­å®šã™ã‚‹)
      const modalCloses = modal.querySelectorAll('[data-js-modal-close]');
      modalCloses.forEach(close => {
        close.addEventListener('click', () => {
          console.log('ãƒ¢ãƒ¼ãƒ€ãƒ«é–‰ã˜ã‚‹ãƒœã‚¿ãƒ³ã‚¯ãƒªãƒƒã‚¯');
          this.#closeModal(modal);
        });
      });
    }

    // ãƒ¢ãƒ¼ãƒ€ãƒ«è¨­å®š(ã‚³ãƒ³ã‚¹ãƒˆãƒ©ã‚¯ã‚¿ã‹ã‚‰å‘¼ã³å‡ºã—)
    #setModals() {
      this.#modals.forEach(modal => {
        this.#setModalCloseEvent(modal);
      });
    }

    // ====================================
    //ã‚¤ãƒ³ã‚¹ã‚¿ãƒ³ã‚¹åŒ–ã—ã¦ã„ã‚‹å ´åˆã®ãƒ¡ã‚½ãƒƒãƒ‰
    
    // ãƒ¢ãƒ¼ãƒ€ãƒ«é–‹ããƒœã‚¿ãƒ³ã®ã‚¤ãƒ™ãƒ³ãƒˆè¨­å®š
    setModalOpenBtn(modalBtn) {
      this.#setModalOpenBtnEvent(modalBtn);
    }

    // ãƒ¢ãƒ¼ãƒ€ãƒ«æœ¬ä½“ã®ã‚¤ãƒ™ãƒ³ãƒˆè¨­å®š
    setModal(modal){
      this.#setModalCloseEvent(modal);
    }

    // ãƒ¢ãƒ¼ãƒ€ãƒ«é–‹ããƒœã‚¿ãƒ³ã¨ãƒ¢ãƒ¼ãƒ€ãƒ«æœ¬ä½“ã®è¨­å®š
    modal(modalObj) {
      console.log('ãƒ¢ãƒ¼ãƒ€ãƒ«ä½œæˆå‘¼ã³å‡ºã—');
      this.setModalOpenBtn(modalObj.modalOpenBtn);
      this.setModal(modalObj.modal);
    }

  }

